getAllMovies():Observable<Imovie[]>{
  return of(this.moviesArr)
}
addMovies(obj : Imovie):Observable<Imovie>{
  this.moviesArr.unshift(obj)
  return of(obj)
}
removeMovie(obj:Imovie){
  let getIndex = this.moviesArr.findIndex(f=>f.id === obj.id)
  this.moviesArr.splice(getIndex,1)
  return(obj)
}
updateMovie(obj:Imovie){
  let getIndex = this.moviesArr.findIndex(f=>f.id === obj.id)
  this.moviesArr[getIndex] = obj
  return of(obj)
}
